package tech.antibytes.wikidata.app.qr.transfer.service

import com.google.zxing.BarcodeFormat
import com.google.zxing.EncodeHintType
import com.google.zxing.common.BitMatrix
import com.google.zxing.qrcode.QRCodeWriter
import io.mockk.every
import io.mockk.mockk
import kotlinx.coroutines.runBlocking
import org.junit.Test
import tech.antibytes.util.test.fixture.fixture
import tech.antibytes.util.test.fixture.kotlinFixture
import tech.antibytes.util.test.fulfils
import tech.antibytes.util.test.mustBe
import tech.antibytes.wikidata.app.qr.transfer.QrCodeServiceContract
import java.util.Base64

class QrCodeServiceSpec {
    private val fixture = kotlinFixture()
    private val encodedMatrix = ""

    @Test
    fun `It fulfils QrCodeServiceContract`() {
        QrCodeService() fulfils QrCodeServiceContract::class
    }

    @Test
    fun `Given create is called it creates a ByteArray`() {
        // Given
        val url: String = fixture.fixture()

        val result = BitMatrix(
            175,
            175
        )

        var idx = 0
        for (x in 0 until 175) {
            for (y in 0 until 175) {
                result.set(x, y)
                idx++
            }
        }

        val writer: QRCodeWriter = mockk()

        every {
            writer.encode(
                "url:$url",
                BarcodeFormat.QR_CODE,
                175,
                175,
                mapOf(EncodeHintType.MARGIN to 1)
            )
        } returns result

        // When
        runBlocking {
            val actual = QrCodeService().create(url)

            // Then
            val encoded = Base64.getEncoder().encodeToString(actual)

            encoded.length mustBe encodedMatrix.length
            encoded.substring(0, 200) mustBe encodedMatrix.substring(0, 200)
        }
    }
}
